'use strict';

// 
window.accordion = (function () {
  //  
  var accordionItems = document.querySelectorAll('.accordion');
  //  
  var accordionTitles = document.querySelectorAll('.accordion__title');

  //   
  var closeAccordionItem = function (element) {
    if (element && element.classList.contains('accordion')) {
      element.classList.remove('accordion--opened');
    }
  };

  //   
  var openAccordionItem = function (element) {
    if (element && element.classList.contains('accordion')) {
      element.classList.add('accordion--opened');
    }
  };

  //    
  var closeAccordionItems = function () {
    if (accordionItems && accordionItems.length > 0) {
      for (var i = 0; i < accordionItems.length; i++) {
        closeAccordionItem(accordionItems[i]);
      }
    }
  };

  //        
  var toggleAccordionItem = function (element) {
    if (element && element.classList.contains('accordion')) {
      if (!element.classList.contains('accordion--opened')) {
        // closeAccordionItems();
        openAccordionItem(element);
      } else {
        closeAccordionItem(element);
      }
    }
  };

  //      
  // (    )
  var addAccordionTitleClickHandler = function (element) {
    if (element) {
      element.addEventListener('click', function (evt) {
        toggleAccordionItem(evt.target.parentElement);
      });

      element.addEventListener('keydown', function (evt) {
        if (evt.key === 'Enter' || evt.key === 'Spacebar' || evt.key === ' ') {
          evt.preventDefault();
          toggleAccordionItem(evt.target.parentElement);
        }
      });
    }
  };

  //  
  var initAccordion = function () {
    closeAccordionItems();
    if (accordionTitles && accordionTitles.length > 0) {
      for (var i = 0; i < accordionTitles.length; i++) {
        addAccordionTitleClickHandler(accordionTitles[i]);
      }
    }
  };

  return {
    initAccordion: initAccordion
  };
})();

'use strict';

//   
window.modal = (function () {
  //    
  var createModal = function (elem, focusId) {
    var modal = {};
    // ,     
    if (!elem.classList.contains('modal')) {
      return false;
    }
    //  
    modal.modal = elem;
    //    
    modal.modalClose = modal.modal.querySelector('.modal__close');
    //   
    modal.modalOverlay = modal.modal.querySelector('.modal__overlay');
    //    
    modal.modalOpenButtons = document.querySelectorAll('[data-modal=' + modal.modal.id);
    if (focusId) {
      modal.focusField = document.querySelector('#' + focusId);
    }

    //   
    modal.showModal = function () {
      modal.modal.classList.add('modal--show');
      document.body.classList.add('body-modal-open');
      if (focusField) {
        focusField.focus();
      }
    };

    //   
    modal.hideModal = function () {
      modal.modal.classList.remove('modal--show');
      document.body.classList.remove('body-modal-open');
    };

    //   
    modal.initModal = function () {
      //       
      if (modal.modalOpenButtons) {
        for (var i = 0; i < modal.modalOpenButtons.length; i++) {
          modal.modalOpenButtons[i].addEventListener('click', function (evt) {
            evt.preventDefault();
            modal.showModal();
          });
        }
      }

      //     
      if (modal.modalClose) {
        modal.modalClose.addEventListener('click', function () {
          modal.hideModal();
        });
      }

      //     
      if (modal.modalOverlay) {
        modal.modalOverlay.addEventListener('click', function () {
          modal.hideModal();
        });
      }

      //    Esc
      document.addEventListener('keydown', function (evt) {
        if (evt.key === 'Escape') {
          evt.preventDefault();
          modal.hideModal();
        }
      });
    };

    return modal;
  }



  return {
    createModal: createModal
  };
})();

'use strict';

//    
window.formStorage = (function () {
  //     localStorage
  var saveFields = function (form, fieldsNames) {
    if (form && fieldsNames) {
      for (var i = 0; i < fieldsNames.length; i++) {
        var currFieldSelector = '[name="' + fieldsNames[i] + '"]';
        var currField = form.querySelector(currFieldSelector);
        localStorage.setItem(fieldsNames[i], currField.value);
      }
    }
  };

  //     localStorage
  var getFields = function (fieldsNames) {
    var formData = [];
    if (fieldsNames) {
      for (var i = 0; i < fieldsNames.length; i++) {
        var currFieldName = fieldsNames[i];
        var currFieldData = {
          'name': currFieldName,
          'value': localStorage.getItem(currFieldName)
        };
        formData.push(currFieldData);
      }
    }
    return formData;
  };

  //    
  var setFields = function (form, formData) {
    if (form && formData) {
      for (var i = 0; i < formData.length; i++) {
        var currFieldSelector = '[name="' + formData[i].name + '"]';
        var currField = form.querySelector(currFieldSelector);
        currField.value = formData[i].value;
      }
    }
  };

  //    
  // form -  
  // fieldsNames -   names ,    
  var initFormStorage = function (form, fieldsNames) {
    if (form && fieldsNames) {
      var formData = getFields(fieldsNames);
      setFields(form, formData);

      form.addEventListener('submit', function () {
        saveFields(form, fieldsNames);
      });
    }
  };

  return {
    initFormStorage: initFormStorage
  };
})();

'use strict';

//  
window.slider = (function () {

  //   
  var createSliderInstance = function (sliderSel) {
    //        
    this.element = document.querySelector(sliderSel);
    if (!this.element) {
      return false;
    }
    //   Splide
    this.splide = null;
    //  
    this.pagination = null;
    //    
    this.paginationList = null;
    //       
    this.paginationItems = [];
    //      
    this.paginationButtons = [];
    //  
    this.paginationCount = null;

    //      Splide
    this.createSplideSlider = function () {
      this.splide = new Splide (this.element, {
        perPage: 4,
        keyboard: 'focused',
        breakpoints: {
          1023: {
            perPage: 2
          },
          767: {
            perPage: 1
          }
        },
        pagination: false
      });
      this.splide.mount();
    }

    //     
    this.addPaginationEventHandlers = function () {
      // var buttons = paginationElem.querySelectorAll('.products-slider__pages-button');

      for (var i = 0; i < this.paginationButtons.length; i++) {
        var paginationButton = this.paginationButtons[i];
        var splide = this.splide;
        var addButtonClickHandler = function (i) {
          paginationButton.addEventListener('click', function () {
            splide.go('>' + i);
          });
        }

        addButtonClickHandler(i);
      }

      var paginationButtons = this.paginationButtons;
      var splide = this.splide;
      this.splide.on('moved', function(newIndex, oldIndex) {
        var newPage = Math.floor(newIndex / splide.options.perPage);
        var oldPage = Math.floor(oldIndex / splide.options.perPage);
        paginationButtons[oldPage].classList.remove('products-slider__pages-button--current');
        paginationButtons[newPage].classList.add('products-slider__pages-button--current');
      });
    }

    //     
    this.generatePagination = function () {
      this.pagination = document.createDocumentFragment();
      //   
      this.paginationList = document.createElement('ul');
      this.paginationList.classList.add('products-slider__pages-list');
      this.pagination.appendChild(this.paginationList);

      //  
      this.paginationCount = Math.ceil(this.splide.length / this.splide.options.perPage);

      //     
      for (var i = 0; i < this.paginationCount; i++) {
        //   
        var paginationItem = document.createElement('li');
        paginationItem.classList.add('products-slider__pages-item');
        this.paginationItems.push(paginationItem);

        //   
        var paginationButton = document.createElement('button');
        paginationButton.classList.add('products-slider__pages-button');
        if (this.splide.index === i) {
          paginationButton.classList.add('products-slider__pages-button--current');
        }
        paginationButton.innerText = i + 1;
        this.paginationButtons.push(paginationButton);


        //    
        paginationItem.appendChild(paginationButton);
        //    
        this.paginationList.appendChild(paginationItem);
      }
    }

    //  
    this.initSlider = function () {
      this.createSplideSlider();
      this.generatePagination();
      this.addPaginationEventHandlers();
      this.element.appendChild(this.pagination);
    }

    return this;
  }

  //   
  // var changeActiveButton = function (oldIndex, newIndex) {
  //   this.paginationButtons[oldIndex].classList.remove('products-slider__pages-button--current');
  //   this.paginationButtons[newIndex].classList.add('products-slider__pages-button--current');
  // }

  return {
    createSliderInstance: createSliderInstance
  }
})();

